import React from 'react';
import {
    StyleSheet,
    View,
    ScrollView,
    SafeAreaView,
    TouchableOpacity,
    Text,
    Button,
} from 'react-native';
import SimilarList from 'animo/components/anime_details/SimilarList';
import AnimeInfo from 'animo/components/anime_details/AnimeInfo';
import { FloatingAction } from 'react-native-floating-action';
import RemoveButton from 'animo/components/your_lists/RemoveButton';

const AnimeDetailsScreen = props => {

    const { animeId, routedFrom } = props.route.params;

    return (
        <View style={styles.container}>
            <SafeAreaView>
                <ScrollView>
                    <AnimeInfo
                        id={animeId}
                    />

                    {routedFrom === 'SingleScreen' ?
                        <RemoveButton />
                        : <View></View>
                    }

                    <SimilarList
                        navigation={props.navigation}
                        id={animeId}
                    />

                    <FloatingAction
                        position='right'
                        animated={false}
                        showBackground={false}
                        onPressMain={() => { props.navigation.navigate('AddAnime', { animeId: animeId }) }}
                    />

                </ScrollView>
            </SafeAreaView>
        </View>
    );
}

const styles = StyleSheet.create({

    container: {
        flex: 1,
    },
});

export default AnimeDetailsScreen;
