import React from 'react';
import {
    StyleSheet,
    View,
    ScrollView,
    SafeAreaView,
    Button
} from 'react-native';
import SimilarList from 'animo/components/anime_details/SimilarList';
import AnimeInfo from 'animo/components/anime_details/AnimeInfo';
import RemoveButton from 'animo/components/your_lists/RemoveButton';

const RemoveAnimeScreen = props => {

    const { animeId } = props.route.params;

    return (
        <View style={styles.container}>
            <SafeAreaView>
                <ScrollView>

                    <AnimeInfo
                        id={animeId}
                    />
                    <RemoveButton
                        navigation={props.navigation}
                        id={animeId}
                    />
                    <SimilarList
                        navigation={props.navigation}
                        id={animeId}
                    />


                </ScrollView>
            </SafeAreaView>
        </View>
    );
}

const styles = StyleSheet.create({

    container: {
        flex: 1,
    },
});

export default RemoveAnimeScreen;
