import React from 'react';
import {
    StyleSheet,
    View,
    Text,
    FlatList,
    Button
} from 'react-native';
import Card from 'animo/components/single_list/Card';
import { useSelector } from 'react-redux';
import { MaterialIcons } from '@expo/vector-icons';

const SingleListScreen = props => {

    const { listId } = props.route.params;
    const selectList = useSelector(state => state.anime.yourLists.find(list => list.id == listId));

    return (
        <View style={{ flex: 1 }}>
            <View style={styles.titleContainer}>
                <Text style={styles.title}> {selectList.title} </Text>
            </View>

            <View style={styles.descContainer}>
                <Text style={styles.desc}> {selectList.desc} </Text>
            </View>
            <View style={styles.listContainer}>
                <FlatList
                    showsHorizontalScrollIndicator={false}
                    numColumns={3}
                    data={selectList.list}
                    keyExtractor={item => item._id}
                    renderItem={({ item }) =>
                        <Card
                            from={"SingleScreen"}
                            navigation={props.navigation}
                            image={item.image}
                            title={item.english_title}
                            id={item._id}
                        />
                    }
                />
            </View>
            <View>
            </View>

        </View>
    );
}

const styles = StyleSheet.create({
    titleContainer: {
        marginHorizontal: 10,
        marginVertical: 10,
    },
    title: {
        fontWeight: '500',
        fontSize: 20,
        fontFamily: 'Futura',
    },
    descContainer: {
        marginHorizontal: 10,
        marginVertical: 0,
    },
    desc: {
        fontSize: 12,
    },
    listContainer: {
        marginVertical: 10
    },
});

export default SingleListScreen;
